import React, { useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:4000/api";

function App() {
  // Media
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [uploaderId, setUploaderId] = useState("");
  const [media, setMedia] = useState([]);

  // Quizzes / Games / Donations / Referrals
  const [userId, setUserId] = useState("");
  const [quizId, setQuizId] = useState("");
  const [answers, setAnswers] = useState("");
  const [gameId, setGameId] = useState("");
  const [score, setScore] = useState(0);
  const [donationAmount, setDonationAmount] = useState(0);
  const [donationCurrency, setDonationCurrency] = useState("USD");
  const [referrerId, setReferrerId] = useState("");
  const [newUserId, setNewUserId] = useState("");

  const [status, setStatus] = useState("");

  useEffect(() => {
    fetchMedia();
  }, []);

  const fetchMedia = async () => {
    const res = await axios.get(`${API}/media`);
    setMedia(res.data.items || []);
  };

  // -------- Media Upload & View --------
  const uploadMedia = async () => {
    if (!file) return alert("Select a file");
    const form = new FormData();
    form.append("media", file);
    form.append("title", title || "Untitled");
    form.append("description", desc);
    form.append("uploaderAccountId", uploaderId);

    setStatus("Uploading...");
    try {
      const res = await axios.post(`${API}/upload`, form, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setStatus(JSON.stringify(res.data, null, 2));
      fetchMedia();
    } catch (err) {
      setStatus("Error: " + err.message);
    }
  };

  const viewMedia = async (id) => {
    const viewerAccountId = prompt(
      "Enter your Hedera Testnet account id (0.0.xxxx):"
    );
    if (!viewerAccountId) return;
    const res = await axios.post(`${API}/view`, {
      mediaId: id,
      viewerAccountId,
    });
    alert("View recorded & token rewarded. See console for HCS response.");
    console.log("View response:", res.data);
    fetchMedia();
  };

  // -------- Quiz / Survey --------
  const submitQuiz = async () => {
    if (!userId || !quizId || !answers) return alert("Fill all quiz fields");
    const res = await axios.post(`${API}/answer`, { userId, quizId, answers });
    setStatus(JSON.stringify(res.data, null, 2));
  };

  // -------- Game Completion --------
  const submitGame = async () => {
    if (!userId || !gameId || score <= 0) return alert("Fill all game fields");
    const res = await axios.post(`${API}/game/complete`, {
      userId,
      gameId,
      score,
    });
    setStatus(JSON.stringify(res.data, null, 2));
  };

  // -------- Donation --------
  const submitDonation = async () => {
    if (!userId || donationAmount <= 0) return alert("Fill donation fields");
    const res = await axios.post(`${API}/donate`, {
      donorId: userId,
      amount: donationAmount,
      currency: donationCurrency,
    });
    setStatus(JSON.stringify(res.data, null, 2));
  };

  // -------- Referral --------
  const submitReferral = async () => {
    if (!referrerId || !newUserId) return alert("Fill referral fields");
    const res = await axios.post(`${API}/referral`, { referrerId, newUserId });
    setStatus(JSON.stringify(res.data, null, 2));
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Crisis Solver — Hackathon PoC</h2>

      {/* -------- Media Upload -------- */}
      <h3>Media Upload</h3>
      <input
        placeholder="Uploader Account ID"
        value={uploaderId}
        onChange={(e) => setUploaderId(e.target.value)}
      />
      <input
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Description"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
      />
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={uploadMedia}>Upload Media</button>

      <h3>Media List</h3>
      {media.map((m) => (
        <div
          key={m._id}
          style={{ border: "1px solid #ccc", padding: 8, marginBottom: 8 }}
        >
          <strong>{m.title}</strong> <br />
          {m.description} <br />
          Views: {m.views || 0} <br />
          HCS: {m.hcsResponse ? m.hcsResponse.consensusTimestamp : "—"} <br />
          <button onClick={() => viewMedia(m._id)}>View & Earn Token</button>
        </div>
      ))}

      {/* -------- Quiz / Survey -------- */}
      <h3>Quiz / Survey</h3>
      <input
        placeholder="User ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <input
        placeholder="Quiz ID"
        value={quizId}
        onChange={(e) => setQuizId(e.target.value)}
      />
      <textarea
        placeholder="Answers JSON"
        value={answers}
        onChange={(e) => setAnswers(e.target.value)}
      />
      <button onClick={submitQuiz}>Submit Quiz</button>

      {/* -------- Game -------- */}
      <h3>Game Completion</h3>
      <input
        placeholder="Game ID"
        value={gameId}
        onChange={(e) => setGameId(e.target.value)}
      />
      <input
        type="number"
        placeholder="Score"
        value={score}
        onChange={(e) => setScore(Number(e.target.value))}
      />
      <button onClick={submitGame}>Submit Game</button>

      {/* -------- Donation -------- */}
      <h3>Donation</h3>
      <input
        type="number"
        placeholder="Amount"
        value={donationAmount}
        onChange={(e) => setDonationAmount(Number(e.target.value))}
      />
      <input
        placeholder="Currency"
        value={donationCurrency}
        onChange={(e) => setDonationCurrency(e.target.value)}
      />
      <button onClick={submitDonation}>Donate & Earn Token</button>

      {/* -------- Referral -------- */}
      <h3>Referral</h3>
      <input
        placeholder="Referrer ID"
        value={referrerId}
        onChange={(e) => setReferrerId(e.target.value)}
      />
      <input
        placeholder="New User ID"
        value={newUserId}
        onChange={(e) => setNewUserId(e.target.value)}
      />
      <button onClick={submitReferral}>Submit Referral</button>

      {/* -------- Status -------- */}
      <h3>Status / HCS & Token Logs</h3>
      <pre style={{ whiteSpace: "pre-wrap" }}>{status}</pre>
    </div>
  );
}

export default App;
