import { useState, useEffect } from "react";
import {
  fetchMedia,
  uploadMedia,
  viewMedia,
  submitQuiz,
  submitGame,
  submitDonation,
  submitReferral,
} from "./api";

const App = () => {
  const [media, setMedia] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [uploaderId, setUploaderId] = useState("");
  const [status, setStatus] = useState("");

  // Quiz/Game/Donation/Referral states
  const [userId, setUserId] = useState("");
  const [quizId, setQuizId] = useState("");
  const [answers, setAnswers] = useState("");
  const [gameId, setGameId] = useState("");
  const [score, setScore] = useState(0);
  const [donationAmount, setDonationAmount] = useState(0);
  const [donationCurrency, setDonationCurrency] = useState("USD");
  const [referrerId, setReferrerId] = useState("");
  const [newUserId, setNewUserId] = useState("");

  useEffect(() => {
    loadMedia();
  }, []);

  const loadMedia = async () => {
    const items = await fetchMedia();
    setMedia(items);
  };

  const handleUpload = async () => {
    setStatus("Uploading...");
    try {
      const result = await uploadMedia({
        file,
        title,
        description: desc,
        uploaderId,
      });
      setStatus(`✅ Uploaded: ${result.id}`);
      loadMedia();
    } catch (err) {
      setStatus("❌ Upload failed: " + err.message);
    }
  };

  const handleViewMedia = async (mediaId) => {
    const viewerAccountId = prompt("Enter your Hedera Testnet account ID:");
    if (!viewerAccountId) return;
    setStatus("Recording view...");
    try {
      const result = await viewMedia({ mediaId, viewerAccountId });
      console.log("View response:", result);
      setStatus("✅ View recorded & token rewarded");
      loadMedia();
    } catch (err) {
      setStatus("❌ View failed: " + err.message);
    }
  };

  const handleSubmitQuiz = async () => {
    try {
      const res = await submitQuiz({ userId, quizId, answers });
      setStatus("✅ Quiz submitted: " + JSON.stringify(res, null, 2));
    } catch (err) {
      setStatus("❌ Quiz submission failed: " + err.message);
    }
  };

  const handleSubmitGame = async () => {
    try {
      const res = await submitGame({ userId, gameId, score });
      setStatus("✅ Game submitted: " + JSON.stringify(res, null, 2));
    } catch (err) {
      setStatus("❌ Game submission failed: " + err.message);
    }
  };

  const handleSubmitDonation = async () => {
    try {
      const res = await submitDonation({
        donorId: userId,
        amount: donationAmount,
        currency: donationCurrency,
      });
      setStatus("✅ Donation submitted: " + JSON.stringify(res, null, 2));
    } catch (err) {
      setStatus("❌ Donation failed: " + err.message);
    }
  };

  const handleSubmitReferral = async () => {
    try {
      const res = await submitReferral({ referrerId, newUserId });
      setStatus("✅ Referral submitted: " + JSON.stringify(res, null, 2));
    } catch (err) {
      setStatus("❌ Referral failed: " + err.message);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Crisis Solver PoC</h2>

      {/* Media Upload */}
      <h3>Upload Media</h3>
      <input
        placeholder="Uploader ID"
        value={uploaderId}
        onChange={(e) => setUploaderId(e.target.value)}
      />
      <input
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Description"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
      />
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload</button>

      {/* Media List */}
      <h3>Media List</h3>
      {media.map((m) => (
        <div
          key={m._id}
          style={{ border: "1px solid #ccc", padding: 8, marginBottom: 8 }}
        >
          <strong>{m.title}</strong>
          <p>{m.description}</p>
          <p>Views: {m.views || 0}</p>
          <button onClick={() => handleViewMedia(m._id)}>
            View & Earn Token
          </button>
        </div>
      ))}

      {/* Quiz */}
      <h3>Quiz</h3>
      <input
        placeholder="User ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <input
        placeholder="Quiz ID"
        value={quizId}
        onChange={(e) => setQuizId(e.target.value)}
      />
      <textarea
        placeholder="Answers JSON"
        value={answers}
        onChange={(e) => setAnswers(e.target.value)}
      />
      <button onClick={handleSubmitQuiz}>Submit Quiz</button>

      {/* Game */}
      <h3>Game Completion</h3>
      <input
        placeholder="Game ID"
        value={gameId}
        onChange={(e) => setGameId(e.target.value)}
      />
      <input
        type="number"
        placeholder="Score"
        value={score}
        onChange={(e) => setScore(Number(e.target.value))}
      />
      <button onClick={handleSubmitGame}>Submit Game</button>

      {/* Donation */}
      <h3>Donation</h3>
      <input
        type="number"
        placeholder="Amount"
        value={donationAmount}
        onChange={(e) => setDonationAmount(Number(e.target.value))}
      />
      <input
        placeholder="Currency"
        value={donationCurrency}
        onChange={(e) => setDonationCurrency(e.target.value)}
      />
      <button onClick={handleSubmitDonation}>Donate & Earn Token</button>

      {/* Referral */}
      <h3>Referral</h3>
      <input
        placeholder="Referrer ID"
        value={referrerId}
        onChange={(e) => setReferrerId(e.target.value)}
      />
      <input
        placeholder="New User ID"
        value={newUserId}
        onChange={(e) => setNewUserId(e.target.value)}
      />
      <button onClick={handleSubmitReferral}>Submit Referral</button>

      <h3>Status</h3>
      <pre style={{ whiteSpace: "pre-wrap" }}>{status}</pre>
    </div>
  );
};

export default App;
