import React, { useState, useEffect } from "react";
import {
  fetchMedia,
  uploadMedia,
  viewMedia,
  submitQuiz,
  submitGame,
  submitDonation,
  submitReferral,
  fetchLeaderboard,
  fetchUserBalance,
} from "./api";

function App() {
  const [tab, setTab] = useState("media");

  // Media
  const [media, setMedia] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [uploaderId, setUploaderId] = useState("");

  // Quiz / Game / Donation / Referral
  const [userId, setUserId] = useState("");
  const [quizId, setQuizId] = useState("");
  const [answers, setAnswers] = useState("");
  const [gameId, setGameId] = useState("");
  const [score, setScore] = useState(0);
  const [donationAmount, setDonationAmount] = useState(0);
  const [donationCurrency, setDonationCurrency] = useState("USD");
  const [referrerId, setReferrerId] = useState("");
  const [newUserId, setNewUserId] = useState("");

  // Dashboard
  const [leaderboard, setLeaderboard] = useState([]);
  const [userBalance, setUserBalance] = useState(0);

  // Status messages
  const [status, setStatus] = useState("");

  // Load media
  const loadMedia = async () => {
    const items = await fetchMedia();
    setMedia(items);
  };

  // Load leaderboard & balance
  const loadLeaderboard = async () => setLeaderboard(await fetchLeaderboard());
  const loadBalance = async () => {
    if (userId) setUserBalance(await fetchUserBalance(userId));
  };

  useEffect(() => {
    loadMedia();
    loadLeaderboard();
    loadBalance();
  }, [userId]);

  /** Handlers */
  const handleUpload = async () => {
    setStatus("Uploading media...");
    try {
      const res = await uploadMedia({
        file,
        title,
        description: desc,
        uploaderId,
      });
      setStatus(`✅ Uploaded: ${res.id}`);
      loadMedia();
    } catch (err) {
      setStatus("❌ Upload failed: " + err.message);
    }
  };

  const handleViewMedia = async (mediaId) => {
    const viewerId = prompt("Enter your Hedera Testnet account ID:");
    if (!viewerId) return;
    setStatus("Recording view...");
    try {
      await viewMedia({ mediaId, viewerAccountId: viewerId });
      setStatus("✅ View recorded & token rewarded");
      loadMedia();
    } catch (err) {
      setStatus("❌ View failed: " + err.message);
    }
  };

  const handleSubmitQuiz = async () => {
    setStatus("Submitting quiz...");
    try {
      const res = await submitQuiz({ userId, quizId, answers });
      setStatus("✅ Quiz submitted: " + JSON.stringify(res));
    } catch (err) {
      setStatus("❌ Quiz submission failed: " + err.message);
    }
  };

  const handleSubmitGame = async () => {
    setStatus("Submitting game...");
    try {
      const res = await submitGame({ userId, gameId, score });
      setStatus("✅ Game submitted: " + JSON.stringify(res));
    } catch (err) {
      setStatus("❌ Game submission failed: " + err.message);
    }
  };

  const handleSubmitDonation = async () => {
    setStatus("Submitting donation...");
    try {
      const res = await submitDonation({
        donorId: userId,
        amount: donationAmount,
        currency: donationCurrency,
      });
      setStatus("✅ Donation submitted: " + JSON.stringify(res));
    } catch (err) {
      setStatus("❌ Donation failed: " + err.message);
    }
  };

  const handleSubmitReferral = async () => {
    setStatus("Submitting referral...");
    try {
      const res = await submitReferral({ referrerId, newUserId });
      setStatus("✅ Referral submitted: " + JSON.stringify(res));
    } catch (err) {
      setStatus("❌ Referral failed: " + err.message);
    }
  };

  /** UI Tabs */
  const tabs = ["media", "quiz", "game", "donation", "referral", "dashboard"];

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Crisis Solver Hackathon PoC</h1>

      {/* Tab Buttons */}
      <div className="flex space-x-2 mb-6">
        {tabs.map((t) => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`px-3 py-1 rounded ${
              tab === t ? "bg-blue-600 text-white" : "bg-gray-200"
            }`}
          >
            {t.charAt(0).toUpperCase() + t.slice(1)}
          </button>
        ))}
      </div>

      <div className="bg-white p-4 rounded shadow">
        {/* Media Tab */}
        {tab === "media" && (
          <div>
            <h3 className="font-bold mb-2">Upload Media</h3>
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Uploader ID"
              value={uploaderId}
              onChange={(e) => setUploaderId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
              className="border p-1 mb-1 w-full"
              placeholder="Description"
              value={desc}
              onChange={(e) => setDesc(e.target.value)}
            />
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              className="mb-2"
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded"
              onClick={handleUpload}
            >
              Upload
            </button>

            <h4 className="mt-4 font-semibold">Media List</h4>
            {media.map((m) => (
              <div key={m._id} className="border p-2 my-2 rounded shadow-sm">
                <strong>{m.title}</strong>
                <p>{m.description}</p>
                <p>Views: {m.views || 0}</p>
                <button
                  className="bg-green-600 text-white px-2 py-1 mt-1 rounded"
                  onClick={() => handleViewMedia(m._id)}
                >
                  View & Earn Token
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Quiz Tab */}
        {tab === "quiz" && (
          <div>
            <h3 className="font-bold mb-2">Submit Quiz</h3>
            <input
              className="border p-1 mb-1 w-full"
              placeholder="User ID"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Quiz ID"
              value={quizId}
              onChange={(e) => setQuizId(e.target.value)}
            />
            <textarea
              className="border p-1 mb-1 w-full"
              placeholder="Answers JSON"
              value={answers}
              onChange={(e) => setAnswers(e.target.value)}
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded"
              onClick={handleSubmitQuiz}
            >
              Submit Quiz
            </button>
          </div>
        )}

        {/* Game Tab */}
        {tab === "game" && (
          <div>
            <h3 className="font-bold mb-2">Submit Game Score</h3>
            <input
              className="border p-1 mb-1 w-full"
              placeholder="User ID"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Game ID"
              value={gameId}
              onChange={(e) => setGameId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              type="number"
              placeholder="Score"
              value={score}
              onChange={(e) => setScore(Number(e.target.value))}
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded"
              onClick={handleSubmitGame}
            >
              Submit Game
            </button>
          </div>
        )}

        {/* Donation Tab */}
        {tab === "donation" && (
          <div>
            <h3 className="font-bold mb-2">Donation</h3>
            <input
              className="border p-1 mb-1 w-full"
              placeholder="User ID"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              type="number"
              placeholder="Amount"
              value={donationAmount}
              onChange={(e) => setDonationAmount(Number(e.target.value))}
            />
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Currency"
              value={donationCurrency}
              onChange={(e) => setDonationCurrency(e.target.value)}
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded"
              onClick={handleSubmitDonation}
            >
              Donate & Earn Token
            </button>
          </div>
        )}

        {/* Referral Tab */}
        {tab === "referral" && (
          <div>
            <h3 className="font-bold mb-2">Referral</h3>
            <input
              className="border p-1 mb-1 w-full"
              placeholder="Referrer ID"
              value={referrerId}
              onChange={(e) => setReferrerId(e.target.value)}
            />
            <input
              className="border p-1 mb-1 w-full"
              placeholder="New User ID"
              value={newUserId}
              onChange={(e) => setNewUserId(e.target.value)}
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded"
              onClick={handleSubmitReferral}
            >
              Submit Referral
            </button>
          </div>
        )}

        {/* Dashboard Tab */}
        {tab === "dashboard" && (
          <div>
            <h3 className="font-bold mb-2">Dashboard</h3>
            <p className="mb-2">User Balance: {userBalance}</p>
            <h4 className="font-semibold">Leaderboard</h4>
            {leaderboard.map((u, i) => (
              <div key={u.userId} className="border p-2 my-1 rounded">
                #{i + 1} - {u.userId}: {u.score} points
              </div>
            ))}
          </div>
        )}

        {/* Status */}
        <div className="mt-4 p-2 border rounded bg-gray-100">
          <strong>Status:</strong>
          <pre className="whitespace-pre-wrap">{status}</pre>
        </div>
      </div>
    </div>
  );
}

export default App;
