import React, { useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:4000/api";

function App() {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [uploaderAccountId, setUploaderAccountId] = useState("");
  const [status, setStatus] = useState(null);
  const [media, setMedia] = useState([]);

  useEffect(() => {
    fetchMedia();
  }, []);

  const fetchMedia = async () => {
    const res = await axios.get(`${API}/media`);
    setMedia(res.data.items || []);
  };

  const upload = async () => {
    if (!file) return alert("Select a file");
    const form = new FormData();
    form.append("media", file);
    form.append("title", title || "Untitled");
    form.append("description", desc);
    form.append("uploaderAccountId", uploaderAccountId || "");

    setStatus("Uploading...");
    try {
      const res = await axios.post(`${API}/upload`, form, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setStatus(JSON.stringify(res.data, null, 2));
      fetchMedia();
    } catch (err) {
      setStatus("Error: " + err.message);
    }
  };

  const view = async (id) => {
    const viewerAccountId = prompt(
      "Enter your Hedera Testnet account id to receive token (0.0.xxxx)"
    );
    if (!viewerAccountId) return;
    const res = await axios.post(`${API}/view`, {
      mediaId: id,
      viewerAccountId,
    });
    alert("View recorded. HCS response and token tx in response. See console.");
    console.log("View response", res.data);
    fetchMedia();
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Crisis Solver — PoC</h2>
      <div>
        <input
          type="text"
          placeholder="Uploader Hedera account id (0.0.xxxx)"
          value={uploaderAccountId}
          onChange={(e) => setUploaderAccountId(e.target.value)}
          style={{ width: 400 }}
        />
      </div>
      <div style={{ marginTop: 10 }}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div>
        <textarea
          placeholder="Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
        />
      </div>
      <div>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button onClick={upload}>Upload</button>
      </div>

      <div style={{ marginTop: 20 }}>
        <h3>Media</h3>
        {media.map((m) => (
          <div
            key={m._id}
            style={{ border: "1px solid #ccc", padding: 8, marginBottom: 8 }}
          >
            <div>
              <strong>{m.title}</strong>
            </div>
            <div>{m.description}</div>
            <div>views: {m.views || 0}</div>
            <div>
              hcs: {m.hcsResponse ? m.hcsResponse.consensusTimestamp : "—"}
            </div>
            <button onClick={() => view(m._id)}>Simulate View & Reward</button>
          </div>
        ))}
      </div>

      <div style={{ whiteSpace: "pre-wrap", marginTop: 20 }}>{status}</div>
    </div>
  );
}

export default App;
